targets.expr:
label_replace(
    count by(k8s_pod_name, process_runtime_version) (
        target_info{environment="$namespace", job="$service"}
    ), 
    "service_instance_id", "$1", "k8s_pod_name", "(.*)"
)

or 

label_replace(
    label_replace(
        count by (pod, version) (jvm_info{namespace="$namespace", service="$service"}), 
        "service_instance_id", "$1", "pod", "(.+)"), 
    "process_runtime_version", "$1", "version", "(.+)"
)

